import React from 'react'
import Input from '../../component/Input/Input'
import Button from '../../component/Button'

const Form = (
    isSignInPage=false,
) => {
    const [data,setData]= useState({
        ...(!isSignInPage && {
            fullName:''
        }),
        email:'',
        password:'',
    })
    return (
        <div className="bg-white w-[600px] h-[700px] shadow-lg rounded-lg flex flex-col justify-center items-center">
            <div className="text-4xl font-extrabold">Welcome {isSignInPage && 'Back'}</div>
            <div className="text-xl font-light">{isSignInPage ? 'Sign in to get explored':'Sign Up to get started'}</div>
            {!isSignInPage && <Input label="Full Name" name="name" placeholder="Enter Your Full name"/>}
            <Input label="Email address" name="email" placeholder="Enter Your Email"/>
            <Input label="Password" name="password" placeholder="Enter Your Password" className="mb-14"/>
            <Button label={isSignInPage ? 'Sign in':'Sign Up'} className="w-1/2 mb-2"/>
            <div>{isSignInPage ? "Did'nt have an account?":"Already have an account?"} <span className="text-primary cursor-pointer underline">{isSignInPage ? 'sign up' : 'Sign in'}</span></div>
        </div>
    )
}

export default Form
