import React, { useState } from 'react';
import Input from '../../component/Input/Input';
import Button from '../../component/Button';

const Form = ({ isSignInPage = true }) => {
    const [data, setData] = useState({
        ...(!isSignInPage && {
            fullName: ''
        }),
        email: '',
        password: '',
    });

    console.log('data :>>', data);

    return (
        <div className="bg-white w-[600px] h-[700px] shadow-lg rounded-lg flex flex-col justify-center items-center">
            <div className="text-4xl font-extrabold">Welcome {isSignInPage && 'Back'}</div>
            <div className="text-xl font-light">
                {isSignInPage ? 'Sign in to get explored' : 'Sign Up to get started'}
            </div>
            <form className="flex flex-col items-center w-full" onSubmit={() => console.log('Submitted')}>
                {!isSignInPage && (
                    <Input
                        label="Full Name"
                        name="name"
                        placeholder="Enter Your Full name"
                        className="mb-6"
                        value={data.fullName}
                        onChange={(e) => setData({ ...data, fullName: e.target.value })}
                    />
                )}
                <Input
                    label="Email address"
                    type="email"
                    name="email"
                    placeholder="Enter Your Email"
                    className="mb-6"
                    value={data.email}
                    onChange={(e) => setData({ ...data, email: e.target.value })}
                />
                <Input
                    label="Password"
                    type="password"
                    name="password"
                    placeholder="Enter Your Password"
                    className="mb-14"
                    value={data.password}
                    onChange={(e) => setData({ ...data, password: e.target.value })}
                />
                <Button
                    label={isSignInPage ? 'Sign in' : 'Sign Up'}
                    type="submit"
                    className="w-1/2 mb-2"
                />
            </form>
            <div>
                {isSignInPage ? "Didn't have an account?" : "Already have an account?"} 
                <span className="text-primary cursor-pointer underline">
                    {isSignInPage ? 'Sign up' : 'Sign in'}
                </span>
            </div>
        </div>
    );
};

export default Form;
